        [Theory]
        [MemberData(nameof(UnitTypes))]
        public void ToUnit_FromNonBaseUnit_ReturnsQuantityWithGivenUnit(JerkUnit unit)
        {
            // See if there is a unit available that is not the base unit.
            var fromUnit = Jerk.Units.FirstOrDefault(u => u != Jerk.BaseUnit && u != JerkUnit.Undefined);

            // If there is only one unit for the quantity, we must use the base unit.
            if (fromUnit == JerkUnit.Undefined)
                fromUnit = Jerk.BaseUnit;

            var quantity = Jerk.From(3.0, fromUnit);
            var converted = quantity.ToUnit(unit);
            Assert.Equal(converted.Unit, unit);
        }

