            LinearPowerDensity twoWattsPerMeter = LinearPowerDensity.FromWattsPerMeter(2);

            Assert.True(oneWattPerMeter < twoWattsPerMeter);
            Assert.True(oneWattPerMeter <= twoWattsPerMeter);
