                case RotationalStiffnessUnit.CentinewtonMillimeterPerDegree: return (_value * 180 / Math.PI * 0.001) * 1e-2d;
                case RotationalStiffnessUnit.CentinewtonMillimeterPerRadian: return (_value * 0.001) * 1e-2d;
